	public position_struct[] getPositionStatisticsCom() {
		/*PROTECTED REGION ID(ProxyComunicationComponent.getPositionStatisticsCom) ENABLED START*/
		return new position_struct[0];
		/*PROTECTED REGION END*/
	}

